function populateSelect(){xhr.onreadystatechange=function(){if(xhr.readyState===XMLHttpRequest.DONE&&200===xhr.status){var e=document.getElementById("sel"),a=JSON.parse(xhr.responseText);localStorage.setItem("dataSource",JSON.stringify(a));for(var t=0;t<a.players.length;t++)e.innerHTML=e.innerHTML+"<option value='"+a.players[t].player.id+"'>"+a.players[t].player.name.first+" "+a.players[t].player.name.last+"</option>"}},xhr.open(method,url,!0),xhr.send()}function show(e){for(var a=JSON.parse(localStorage.getItem("dataSource")),t=document.getElementsByClassName("player__name"),r=document.getElementsByClassName("player__pos"),s=document.getElementsByClassName("player__club"),n=document.getElementsByClassName("player__img"),l=document.getElementsByClassName("player__apps"),o=document.getElementsByClassName("player__goals"),p=document.getElementsByClassName("player__assists"),m=document.getElementsByClassName("player__wins"),y=document.getElementsByClassName("player__mp"),i=0;i<a.players.length;i++)if(a.players[i].player.id==e.value){document.getElementById("player-profile").classList.add("show");for(var u=a.players[i].player,c=a.players[i],g=0;g<t.length;g++)t[g].innerHTML=e.options[e.selectedIndex].text;for(var g=0;g<r.length;g++)r[g].innerHTML=u.info.positionInfo.split(" ").slice(-1);for(var g=0;g<s.length;g++){var d=u.currentTeam.name,h=d.replace(" ","-").toLowerCase();s[g].innerHTML="<span class='"+h+"'></span>"}for(var g=0;g<n.length;g++)n[g].src="images/p"+e.value+".png";for(var g=0;g<l.length;g++)l[g].querySelector("span").innerHTML=c.stats[6].value;for(var g=0;g<o.length;g++)o[g].querySelector("span").innerHTML=c.stats[0].value;for(var g=0;g<p.length;g++)p[g].querySelector("span").innerHTML=c.stats[5].value;for(var g=0;g<m.length;g++)m[g].querySelector("span").innerHTML=c.stats[2].value;for(var g=0;g<y.length;g++)y[g].querySelector("span").innerHTML=c.stats[7].value}}const xhr=new XMLHttpRequest,method="GET",overrideMimeType="application/json",url="/player-stats.json";window.onload=populateSelect();